package hxw.test.springbootunittesting.service.impl;import static org.assertj.core.api.Assertions.assertThat;import hxw.test.springbootunittesting.domain.SaveUserPort;import hxw.test.springbootunittesting.domain.SendMailPort;import hxw.test.springbootunittesting.domain.User;import java.time.LocalDateTime;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.Mockito;import org.mockito.junit.jupiter.MockitoExtension;import org.mockito.stubbing.Answer;@ExtendWith(MockitoExtension.class)public class UserServiceImplUsingMockitoAnnotationTest {    @Mock    private SaveUserPort saveUserPort;    @Mock    private SendMailPort sendMailPort;    @InjectMocks    private UserServiceImpl userService;    /**     * using mockito @Mock annotation     */    @Test    void savedUserHasRegistrationDateWithAnnotation() {        User user = new User("huangxuewei", "huangxueweihxw@gmail.com");        user.setRegistrationDate(LocalDateTime.now());        Mockito.when(saveUserPort.saveUser(Mockito.any(User.class))).then((Answer<Long>) a -> 42L);        Long userId = userService.registerUser(user, false);        assertThat(userId).isNotNull();    }}